import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState({});
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState("");
  const [showAddOptions, setShowAddOptions] = useState(false); // To toggle '+' options
  const [showGroupModal, setShowGroupModal] = useState(false); // To toggle group creation modal
  const [groupMembers, setGroupMembers] = useState([]); // Selected members for a group

  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("userList", (userList) => {
      setUsers(userList);
    });

    socket.on("receiveMessage", ({ sender, message }) => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [sender]: [...(prevMessages[sender] || []), { sender, message }],
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (msg.trim() && selectedUser) {
      socket.emit("sendMessage", { sender: username, recipient: selectedUser, message: msg });
      setMessages((prevMessages) => ({
        ...prevMessages,
        [selectedUser]: [...(prevMessages[selectedUser] || []), { sender: username, message: msg }],
      }));
    }
  };

  const handleCreateGroup = () => {
    console.log("Group members selected:", groupMembers);
    alert(`Group created with members: ${groupMembers.join(", ")}`);
    setShowGroupModal(false);
  };

  if (step === 2) {
    return (
      <div className="chat-app">
        <div className="left-panel">
          <header>
            Chats
            <button id="addPeople" onClick={() => setShowAddOptions(true)}>+</button>
          </header>
          {users.map((user) => (
            <div
              key={user}
              className={`user-item ${user === selectedUser ? "active" : ""}`}
              onClick={() => setSelectedUser(user)}
            >
              <p>{user}</p>
            </div>
          ))}
        </div>
        <div className="right-panel">
          {selectedUser ? (
            <>
              <header>{selectedUser}</header>
              <MainDiv messages={messages[selectedUser] || []} username={username} />
              <ChatInput sendMessage={handleSendMessage} />
            </>
          ) : (
            <div className="no-chat-selected">
              <p>Select a user to start chatting</p>
            </div>
          )}
        </div>

        {showAddOptions && (
          <div className="modal">
            <button onClick={() => alert("Add New User functionality here")}>Add New User</button>
            <button onClick={() => setShowGroupModal(true)}>Create Group</button>
            <button onClick={() => setShowAddOptions(false)}>Close</button>
          </div>
        )}

        {showGroupModal && (
          <div className="modal">
            <h2>Select Members for Group</h2>
            {users.map((user) => (
              <div key={user}>
                <input
                  type="checkbox"
                  value={user}
                  onChange={(e) => {
                    const selected = e.target.value;
                    setGroupMembers((prev) =>
                      prev.includes(selected)
                        ? prev.filter((member) => member !== selected)
                        : [...prev, selected]
                    );
                  }}
                />
                <label>{user}</label>
              </div>
            ))}
            <button onClick={handleCreateGroup}>Create Group</button>
            <button onClick={() => setShowGroupModal(false)}>Cancel</button>
          </div>
        )}
      </div>
    );
  }
  return <div>Welcome to Chat App</div>;
}

export default App;
