import { useState, useEffect } from "react";

import axios from 'axios';

function ChatInput() {
    const [message, setMessage] = useState('');
    const [messageList, setMessageList] = useState([]);
    const [selectedOption, setSelectedOption] = useState("");

    useEffect(()=>{
        const getMessage= async() => {
            const resp = await axios.get('http://localhost:4000/api')
            setMessageList(resp.data.messages)

        }
        getMessage()

    },[])

    console.log("message", messageList);


    const handleChange = (event) => {
        setMessage(event.target.value);
    };

    const handleSubmit = () => {
        console.log('Message sent: ', message);
        setMessageList((prev) => ([...prev,{ message:message ,createdAt : new Date()}]))
        setMessage('');
    };
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit();
        }
    }

    return (
        <>  
             <div>
            <label htmlFor="dropdown">Choose an option:</label>
            <select id="dropdown" value={selectedOption} onChange={handleChange}>
                <option value="">--Select--</option>
                <option value="option1">Option 1</option>
                <option value="option2">Option 2</option>
                <option value="option3">Option 3</option>
            </select>

            <p>You selected: {selectedOption}</p>
        </div>

            <div>

                {messageList.map(
                    (element, index) => {
                        return (<li key={index}>{element.message} </li>)
                    }
                )}
                <input id="ip"
                    type="text"
                    value={message}
                    onChange={handleChange}
                    onKeyDown={handleKeyDown}
                    placeholder="Type a message..."
                    className="chat-input"
                />
                <button id="sendBtn" onClick={handleSubmit}> <img id="senimg" src="src/images/buttonsend.png" alt="=>"></img> </button>
            </div>
        </>
    );
}

export default ChatInput;
