import React, { useEffect, useState } from "react";
import ChatInput from "./ChatInput";
import { io } from "socket.io-client";

const socket = io("http://localhost:4000");

function MainDiv({ username }) {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        socket.on("messageResponse", (newMessages) => {
            setMessages((prevMessages) => [...prevMessages, ...newMessages]);
        });

        return () => socket.disconnect();
    }, []);

    const handleSendMessage = (message) => {
        const newMessage = { username, text: message };
        socket.emit("message", JSON.stringify(newMessage));
    };

    return (
        <div style={{ display: "flex", flexDirection: "column", height: "100vh", padding: "10px" }}>
            <div
                style={{
                    flex: 1,
                    overflowY: "auto",
                    padding: "10px",
                    border: "1px solid black",
                    backgroundColor: "#f9f9f9",
                }}
            >
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        style={{
                            textAlign: msg.username === username ? "right" : "left",
                            margin: "5px 0",
                            padding: "10px",
                            backgroundColor: msg.username === username ? "#d1ffd6" : "#e6e6e6",
                            borderRadius: "10px",
                            display: "inline-block",
                        }}
                    >
                        {msg.username !== username && <strong>{msg.username}: </strong>}
                        {msg.text}
                    </div>
                ))}
            </div>
            <ChatInput onSend={handleSendMessage} />
        </div>
    );
}

export default MainDiv;
