import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import MainDiv from "./components/mainDiv";
import ChatInput from "./components/ChatInput";
import "./index.css";

const socket = io("http://localhost:4000");

function App() {
  const [messages, setMessages] = useState([]);
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [isJoined, setIsJoined] = useState(false);
  const [generatedRoom, setGeneratedRoom] = useState("");

  useEffect(() => {
    socket.on("messageResponse", (message) => {
      setMessages((prev) => [...prev, message]);
    });

    socket.on("user-joined", (notification) => {
      setMessages((prev) => [...prev, { username: "System", text: notification }]);
    });

    socket.on("user-left", (notification) => {
      setMessages((prev) => [...prev, { username: "System", text: notification }]);
    });

    socket.on("room-created", (roomID) => {
      setGeneratedRoom(roomID);
    });

    return () => {
      socket.off("messageResponse");
      socket.off("user-joined");
      socket.off("user-left");
      socket.off("room-created");
    };
  }, []);

  const createRoom = () => {
    if (username.trim()) {
      socket.emit("create-room", username);
      setIsJoined(true);
    }
  };

  const joinRoom = () => {
    if (username.trim() && room.trim()) {
      socket.emit("join-room", { username, room });
      setIsJoined(true);
    }
  };

  const addMessage = (text) => {
    socket.emit("user-message", { room, message: text });
  };

  return (
    <div>
      {!isJoined ? (
        <div className="join-container">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={createRoom}>Create Room</button>
          <input
            type="text"
            placeholder="Enter room ID to join"
            value={room}
            onChange={(e) => setRoom(e.target.value)}
          />
          <button onClick={joinRoom}>Join Room</button>
          {generatedRoom && <p>Share this Room ID: <strong>{generatedRoom}</strong></p>}
        </div>
      ) : (
        <>
          <h1 style={{ textAlign: "center" }}>Chatroom: {room || generatedRoom}</h1>
          <div className="chat-container">
            <div className="chat-messages">
              <MainDiv messages={messages} />
            </div>
            <div className="chat-input">
              <ChatInput addMessage={addMessage} />
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
