import { useState, useEffect } from "react";

import axios from 'axios';

function ChatInput() {
    const [message, setMessage] = useState('');
    const [messageList, setMessageList] = useState([]);

    useEffect(()=>{
        const getMessage= async() => {
            const resp = await axios.get('http://localhost:4000/api')
            setMessageList(resp.data.messages)

        }
        getMessage()

    },[])

    console.log("message", messageList);


    const handleChange = (event) => {
        setMessage(event.target.value);
    };

    const handleSubmit = () => {
        console.log('Message sent: ', message);
        setMessageList((prev) => ([...prev,{ message:message ,createdAt : new Date()}]))
        setMessage('');
    };
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit();
        }
    }

    return (
        <>
            <div>
                {messageList.map(
                    (element, index) => {
                        return (<li key={index}>{element.message} </li>)
                    }
                )}
                <input id="ip"
                    type="text"
                    value={message}
                    onChange={handleChange}
                    onKeyDown={handleKeyDown}
                    placeholder="Type a message..."
                    className="chat-input"
                />
                <button id="sendBtn" onClick={handleSubmit}> <img id="senimg" src="src/images/buttonsend.png" alt="=>"></img> </button>
            </div>
        </>
    );
}

export default ChatInput;
