import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState({});
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState("");
  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("userList", (userList) => {
      setUsers(userList);
    });

    socket.on("receiveMessage", ({ sender, message }) => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [sender]: [...(prevMessages[sender] || []), { sender, message }],
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleAuthSubmit = () => {
    if (authMode === "signup" && username.trim() && email.trim() && password.trim()) {
      alert("User signed up successfully!");
      setStep(2);
    } else if (authMode === "login" && email.trim() && password.trim()) {
      alert("User logged in successfully!");
      setStep(2);
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (msg.trim() && selectedUser) {
      socket.emit("sendMessage", { sender: username, recipient: selectedUser, message: msg });
      setMessages((prevMessages) => ({
        ...prevMessages,
        [selectedUser]: [...(prevMessages[selectedUser] || []), { sender: username, message: msg }],
      }));
    }
  };

  if (step === 1) {
    return (
      <div className="auth-container">
        <h1>Chat App</h1>
        {!authMode && (
          <div>
            <button onClick={() => setAuthMode("signup")}>Signup</button>
            <button onClick={() => setAuthMode("login")}>Login</button>
          </div>
        )}
        {authMode && (
          <>
            {authMode === "signup" && (
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>{authMode === "signup" ? "Signup" : "Login"}</button>
          </>
        )}
      </div>
    );
  }

  return (
    <div className="chat-app">
      <div className="left-sidebar">
        <header>Chats</header>
        {users.map((user) => (
          <div
            key={user}
            className={`user-item ${user === selectedUser ? "active" : ""}`}
            onClick={() => setSelectedUser(user)}
          >
            {user}
          </div>
        ))}
      </div>
      <div className="chat-section">
        {selectedUser ? (
          <>
            <header>{selectedUser}</header>
            <div className="messages">
              {(messages[selectedUser] || []).map((msg, index) => (
                <div
                  key={index}
                  className={`message ${msg.sender === username ? "sent" : "received"}`}
                >
                  {msg.message}
                </div>
              ))}
            </div>
            <div className="input-section">
              <input
                type="text"
                placeholder="Type a message"
                onKeyDown={(e) => {
                  if (e.key === "Enter") handleSendMessage(e.target.value);
                }}
              />
            </div>
          </>
        ) : (
          <div className="no-chat">Select a user to start chatting</div>
        )}
      </div>
    </div>
  );
}

export default App;
