import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [messages, setMessages] = useState([]);
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState(""); // "login" or "signup"
  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    socket.on("connect_error", () => {
      alert("Socket connection failed. Check backend IP or network.");
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleAuthSubmit = () => {
    if (authMode === "signup" && username.trim() && email.trim() && password.trim()) {
      alert("User signed up successfully!");
      setStep(2);
    } else if (authMode === "login" && email.trim() && password.trim()) {
      alert("User logged in successfully!");
      setStep(2);
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (msg.trim()) {
      socket.emit("sendMessage", { username, message: msg });
    } else {
      alert("Message cannot be empty.");
    }
  };

  if (step === 1) {
    return (
      <div className="welcome-container">
        <h1>Welcome to Chat App</h1>
        {!authMode && (
          <div className="button-container">
            <button onClick={() => setAuthMode("signup")}>Signup</button>
            <button onClick={() => setAuthMode("login")}>Login</button>
          </div>
        )}
        {authMode === "signup" && (
          <>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Signup</button>
          </>
        )}
        {authMode === "login" && (
          <>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Login</button>
          </>
        )}
      </div>
    );
  }
  // if(step === 2){
  //   return (
  //     <>

  //     </>
  //   )
  // }

  return (
    <div className="chat-container">
      <header className="chat-header">
        Welcome, {username}
      </header>
      <div className="messages-container">
        <MainDiv messages={messages} username={username} />
      </div>
      <div id="typemessage" className="input-container">
        <ChatInput sendMessage={handleSendMessage} />
      </div>
    </div>
  );
}

export default App;
