import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";
import Allusers from "./components/AllUsers";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState({});
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState("");
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [groupMembers, setGroupMembers] = useState([]);

  const socketRef = useRef(null);

  const users = Allusers; // Use Allusers directly

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("receiveMessage", ({ sender, message }) => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [sender]: [...(prevMessages[sender] || []), { sender, message }],
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleAuthSubmit = () => {
    if (authMode === "signup" && username.trim() && email.trim() && password.trim()) {
      alert("User signed up successfully!");
      setStep(2);
    } else if (authMode === "login" && email.trim() && password.trim()) {
      alert("User logged in successfully!");
      setStep(2);
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (msg.trim() && selectedUser) {
      socket.emit("sendMessage", { sender: username, recipient: selectedUser.name, message: msg });

      setMessages((prevMessages) => ({
        ...prevMessages,
        [selectedUser.name]: [...(prevMessages[selectedUser.name] || []), { sender: username, message: msg }],
      }));
    }
  };

  const handleCreateGroup = () => {
    console.log("Group created with members:", groupMembers);
    alert(`Group created with members: ${groupMembers.join(", ")}`);
    setShowGroupModal(false);
  };

  if (step === 1) {
    return (
      <div className="welcome-container">
        <h1>Welcome to Chat App</h1>
        {!authMode && (
          <div className="button-container">
            <button onClick={() => setAuthMode("signup")}>Signup</button>
            <button onClick={() => setAuthMode("login")}>Login</button>
          </div>
        )}
        {authMode === "signup" && (
          <>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Signup</button>
          </>
        )}
        {authMode === "login" && (
          <>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Login</button>
          </>
        )}
      </div>
    );
  }

  if (step === 2) {
    return (
      <div className="chat-app">
        <div className="left-panel">
          <header>
            Chats
            <button id="addPeople" onClick={() => setShowAddOptions(true)}>
              +
            </button>
          </header>
          {users.map((user) => (
            <div
              key={user.id}
              className={`user-item ${user.id === selectedUser?.id ? "active" : ""}`}
              onClick={() => setSelectedUser(user)}
            >
              <p>{user.name}</p>
            </div>
          ))}
        </div>
        <div className="right-panel">
          {selectedUser ? (
            <>
              <header>{selectedUser.name}</header>
              <MainDiv messages={messages[selectedUser.name] || []} username={username} />
              <ChatInput sendMessage={handleSendMessage} />
            </>
          ) : (
            <div className="no-chat-selected">
              <p>Select a user to start chatting</p>
            </div>
          )}
        </div>

        {showAddOptions && (
          <div className="modal">
            <button className="mBtn" onClick={() => alert("Add New User functionality here")}>Add New User</button>
            <button className="mBtn" onClick={() => setShowGroupModal(true)}>Create Group</button>
            <button id="closebtn" onClick={() => setShowAddOptions(false)}>X</button>
          </div>
        )}

        {showGroupModal && (
          <div className="modal">
            <h2>Select Members for Group</h2>
            {users.map((user) => (
              <div key={user.id}>
                <input
                  type="checkbox"
                  value={user.name}
                  onChange={(e) => {
                    const selected = e.target.value;
                    setGroupMembers((prev) =>
                      prev.includes(selected)
                        ? prev.filter((member) => member !== selected)
                        : [...prev, selected]
                    );
                  }}
                />
                <label>{user.name}</label>
              </div>
            ))}
            <button onClick={handleCreateGroup}>Create Group</button>
            <button onClick={() => setShowGroupModal(false)}>Cancel</button>
          </div>
        )}
      </div>
    );
  }

  return null;
}

export default App;
