import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [roomCreated, setRoomCreated] = useState(false);
  const socketRef = useRef(null);

  useEffect(() => {
    const newSocket = io("http://192.168.101.32:4000", {
      transports: ["websocket", "polling"],
      reconnection: true,
    });
    socketRef.current = newSocket;

    newSocket.on("connect", () => console.log("Connected to the socket server"));

    newSocket.on("receiveMessage", (data) =>
      setMessages((prev) => [...prev, data])
    );

    newSocket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    newSocket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    newSocket.on("errorMessage", (error) => alert(error));
    newSocket.on("connect_error", () =>
      alert("Unable to connect to the server. Please check the backend.")
    );

    return () => newSocket.disconnect();
  }, []);

  const handleCreateRoom = () => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter a username.");
    }
  };

  const handleJoinRoom = () => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (username.trim() && roomId.trim()) {
      socket.emit("joinRoom", { roomID: roomId, username });
    } else {
      alert("Please enter both username and Room ID.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (msg.trim()) {
      socket.emit("sendMessage", { roomID: roomId, username, message: msg });
    } else {
      alert("Message cannot be empty.");
    }
  };

  return (
    <div className="app">
      {!roomCreated ? (
        <div className="welcome-container">
          <h1>Welcome to Chat App</h1>
          <div className="form">
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Room ID (optional)"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            <div className="button-container">
              <button onClick={handleCreateRoom}>Create Room</button>
              <button onClick={handleJoinRoom}>Join Room</button>
            </div>
          </div>
        </div>
      ) : (
        <div className="chat-container">
          <header className="chat-header">Room ID: {roomId}</header>
          <div className="messages-container">
            <MainDiv messages={messages} />
          </div>
          <div className="input-container">
            <ChatInput socket={socketRef.current} roomId={roomId} username={username} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
