import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
// import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";
import Allusers from "./components/AllUsers";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState({});
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState("");
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [groupMembers, setGroupMembers] = useState([]);
  const [groupName, setGroupName] = useState("");
  const [groups, setGroups] = useState([]);

  const socketRef = useRef(null);
  const users = Allusers;

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("receiveMessage", ({ sender, message }) => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [sender]: [...(prevMessages[sender] || []), { sender, message }],
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleAuthSubmit = () => {
    if (authMode === "signup" && username.trim() && email.trim() && password.trim()) {
      alert("User signed up successfully!");
      setStep(2);
    } else if (authMode === "login" && email.trim() && password.trim()) {
      alert("User logged in successfully!");
      setStep(2);
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (msg.trim() && selectedUser) {
      socket.emit("sendMessage", { sender: username, recipient: selectedUser.name, message: msg });

      setMessages((prevMessages) => ({
        ...prevMessages,
        [selectedUser.name]: [...(prevMessages[selectedUser.name] || []), { sender: username, message: msg }],
      }));
    }
  };

  const handleCreateGroup = () => {
    if (!groupName.trim()) {
      alert("Please enter a group name.");
      return;
    }

    if (groupMembers.length === 0) {
      alert("Please select at least one member for the group.");
      return;
    }

    const newGroup = { id: `group-${groups.length + 1}`, name: groupName, members: groupMembers };
    setGroups((prevGroups) => [...prevGroups, newGroup]);
    setGroupMembers([]);
    setGroupName("");
    setShowGroupModal(false);
  };

  if (step === 1) {
    return (
      <div className="welcome-container">
        <h1>Welcome to Chat App</h1>
        {!authMode && (
          <div className="button-container">
            <button onClick={() => setAuthMode("signup")}>Signup</button>
            <button onClick={() => setAuthMode("login")}>Login</button>
          </div>
        )}
        {authMode === "signup" && (
          <>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Signup</button>
          </>
        )}
        {authMode === "login" && (
          <>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Login</button>
          </>
        )}
      </div>
    );
  }

  if (step === 2) {
    return (
      <MainDiv
        users={users}
        groups={groups}
        selectedUser={selectedUser}
        setSelectedUser={setSelectedUser}
        messages={messages}
        username={username}
        handleSendMessage={handleSendMessage}
        showAddOptions={showAddOptions}
        setShowAddOptions={setShowAddOptions}
        showGroupModal={showGroupModal}
        setShowGroupModal={setShowGroupModal}
        handleCreateGroup={handleCreateGroup}
        groupMembers={groupMembers}
        setGroupMembers={setGroupMembers}
        groupName={groupName}
        setGroupName={setGroupName}
      />
    );
  }

  return null;
}

export default App;
