import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [roomCreated, setRoomCreated] = useState(false);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = io("http://192.168.101.32:4000");
    setSocket(newSocket);

    newSocket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    newSocket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    newSocket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    newSocket.on("errorMessage", (error) => {
      alert(error);
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  const handleCreateRoom = () => {
    if (socket && username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Connection issue or missing username. Please refresh the page.");
    }
  };

  const handleJoinRoom = () => {
    if (socket && username.trim() && roomId.trim()) {
      socket.emit("joinRoom", { roomID: roomId, username });
    } else {
      alert("Connection issue or missing details. Please refresh the page.");
    }
  };

  return (
    <div className="app">
      {!roomCreated ? (
        <div className="welcome-container">
          <h1>Welcome to Chat App</h1>
          <div className="form">
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Room ID (optional)"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            <div className="button-container">
              <button onClick={handleCreateRoom}>Create Room</button>
              <button onClick={handleJoinRoom}>Join Room</button>
            </div>
          </div>
        </div>
      ) : (
        <div className="chat-container">
          <header className="chat-header">Room ID: {roomId}</header>
          <div className="messages-container">
            <MainDiv messages={messages} />
          </div>
          <div className="input-container">
            <ChatInput
              addMessage={(msg) =>
                socket.emit("sendMessage", { roomID: roomId, username, message: msg })
              }
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
