// export default App;



import React, { useState } from "react";
import MainDiv from "./components/mainDiv.js";

function App() {
    const [username, setUsername] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        if (username.trim() !== "") {
            setIsLoggedIn(true);
        }
    };

    return (
        <div>
            {!isLoggedIn ? (
                <div style={{ textAlign: "center", marginTop: "100px" }}>
                    <h1>Enter Your Username</h1>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your name"
                        style={{
                            height: "2rem",
                            width: "20rem",
                            fontSize: "18px",
                            borderRadius: "5px",
                            textAlign: "center",
                        }}
                    />
                    <button
                        onClick={handleLogin}
                        style={{
                            marginLeft: "10px",
                            height: "2.5rem",
                            width: "5rem",
                            borderRadius: "5px",
                            fontSize: "16px",
                            backgroundColor: "rgb(100, 149, 237)",
                            color: "white",
                            cursor: "pointer",
                        }}
                    >
                        Join
                    </button>
                </div>
            ) : (
                <MainDiv username={username} />
            )}
        </div>
    );
}

export default App;
