import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";
import Allusers from "./components/AllUsers"

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [users, setUsers] = useState([]); // List of users
  const [selectedUser, setSelectedUser] = useState(null); // Currently selected user for chat
  const [messages, setMessages] = useState({});
  const [step, setStep] = useState(1);
  const [authMode, setAuthMode] = useState(""); // "login" or "signup"
  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("userList", (userList) => {
      setUsers(userList);
    });

    socket.on("receiveMessage", ({ sender, message }) => {
      setMessages((prevMessages) => ({
        ...prevMessages,
        [sender]: [...(prevMessages[sender] || []), { sender, message }],
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleAuthSubmit = () => {
    if (authMode === "signup" && username.trim() && email.trim() && password.trim()) {
      alert("User signed up successfully!");
      setStep(2);
    } else if (authMode === "login" && email.trim() && password.trim()) {
      alert("User logged in successfully!");
      setStep(2);
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (msg.trim() && selectedUser) {
      socket.emit("sendMessage", { sender: username, recipient: selectedUser, message: msg });

      setMessages((prevMessages) => ({
        ...prevMessages,
        [selectedUser]: [...(prevMessages[selectedUser] || []), { sender: username, message: msg }],
      }));
    }
  };

  if (step === 1) {
    return (
      <div className="welcome-container">
        <h1>Welcome to Chat App</h1>
        {!authMode && (
          <div className="button-container">
            <button onClick={() => setAuthMode("signup")}>Signup</button>
            <button onClick={() => setAuthMode("login")}>Login</button>
          </div>
        )}
        {authMode === "signup" && (
          <>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Signup</button>
          </>
        )}
        {authMode === "login" && (
          <>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleAuthSubmit}>Login</button>
          </>
        )}
      </div>
    );
  }

  if (step === 2) {
    return (
      <div className="chat-app">
        <div className="left-panel">
          <header style={{}}>Chats</header>
          {users.map((user) => (
            <div
              key={user}
              className={`user-item ${user === selectedUser ? "active" : ""}`}
              onClick={() => setSelectedUser(user)}
            >
              <p>{user}</p>
            </div>
          ))}
          <div className="all-users">
            <header style={{color: 'white'}}>All Users</header>
            {Allusers.map((user) => (
              <div key={user.id} className="user-info" onClick={() => setSelectedUser(user.name)}>
                <p>{user.name}</p>
                <span>{user.email}</span>
              </div>
            ))}
          </div>
        </div>
        <div className="right-panel">
          {selectedUser ? (
            <>
              <header>{selectedUser}</header>
              <MainDiv messages={messages[selectedUser] || []} username={username} />
              <ChatInput sendMessage={handleSendMessage} />
            </>
          ) : (
            <div className="no-chat-selected">
              <p>Select a user to start chatting</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="chat-app">
      <div className="left-panel">
        {users.map((user) => (
          <div
            key={user}
            className={`user-item ${user === selectedUser ? "active" : ""}`}
            onClick={() => setSelectedUser(user)}
          >
            {user}
          </div>
        ))}
      </div>
      <div className="right-panel">
        {selectedUser ? (
          <>
            <header>{selectedUser}</header>
            <MainDiv messages={messages[selectedUser] || []} username={username} />
            <ChatInput sendMessage={handleSendMessage} />
          </>
        ) : (
          <div>Select a user to chat</div>
        )}
      </div>
    </div>
  );
}

export default App;
