import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [roomCreated, setRoomCreated] = useState(false);
  const [error, setError] = useState("");  // Error state to show error messages
  const socketRef = useRef(null);

  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
      reconnection: true,
    });

    const socket = socketRef.current;

    socket.on("connect", () => {
      console.log("Connected to socket server");
    });

    socket.on("receiveMessage", (data) => {
      if (data.roomID === roomId) {
        setMessages((prevMessages) => [...prevMessages, data]);
      }
    });

    socket.on("receiveMessages", (data) => {
      setMessages(data);
    });

    socket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("errorMessage", (error) => {
      setError(error);  // Handle error received from backend
    });

    socket.on("connect_error", () => {
      alert("Socket connection failed. Check backend IP or network.");
    });

    return () => {
      socket.disconnect();
    };
  }, [roomId]);

  const handleCreateRoom = () => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter a username.");
    }
  };

  const handleJoinRoom = () => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (username.trim() && roomId.trim()) {
      socket.emit("joinRoom", { roomID: roomId, username });
    } else {
      alert("Please enter both username and Room ID.");
    }
  };

  const handleSendMessage = (msg) => {
    const socket = socketRef.current;
    if (!socket?.connected) {
      alert("Socket not connected. Please refresh the page.");
      return;
    }
    if (msg.trim()) {
      const messageData = { roomID: roomId, username, message: msg };
      socket.emit("sendMessage", messageData);
      setMessages((prevMessages) => [...prevMessages, messageData]);
    } else {
      alert("Message cannot be empty.");
    }
  };

  return (
    <div className="app">
      {error && <div className="error-message">{error}</div>} {/* Error message display */}
      {!roomCreated ? (
        <div className="welcome-container">
          <h1>Welcome to Chat App</h1>
          <div className="form">
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Room ID (optional)"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            <div className="button-container">
              <button onClick={handleCreateRoom}>Create Room</button>
              <button onClick={handleJoinRoom}>Join Room</button>
            </div>
          </div>
        </div>
      ) : (
        <div className="chat-container">
          <header className="chat-header">
            <div className="room-info">
              <span>Room Name: {roomId}</span>
              <span>Room ID: {roomId}</span>
            </div>
          </header>
          <div className="messages-container">
            <MainDiv messages={messages} username={username} />
          </div>
          <div className="input-container">
            <ChatInput sendMessage={handleSendMessage} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
