import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

const socket = io("http://<YOUR_BACKEND_IP>:4000"); // Replace with your backend IP

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [roomCreated, setRoomCreated] = useState(false);
  const [isJoiningRoom, setIsJoiningRoom] = useState(false); // Flag to track if joining or creating a room

  // Listen for incoming messages and room events
  useEffect(() => {
    socket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    socket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("errorMessage", (error) => {
      alert(error);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("errorMessage");
    };
  }, []);

  const handleCreateOrJoinRoom = () => {
    if (username.trim()) {
      if (roomId.trim()) {
        // If roomId is entered, join the existing room
        socket.emit("joinRoom", { roomID: roomId, username: username });
        setIsJoiningRoom(true);
      } else {
        // If roomId is not entered, create a new room
        socket.emit("createRoom", username);
      }
    } else {
      alert("Please enter a username");
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      socket.emit("sendMessage", { roomID: roomId, username: username, message: message });
      setMessage(""); // Clear message input after sending
    }
  };

  return (
    <div className="App">
      <h1>Chat Application</h1>

      {!roomCreated ? (
        <div>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Room ID (optional)"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <button onClick={handleCreateOrJoinRoom}>
            {isJoiningRoom ? "Join Room" : "Create Room"}
          </button>
        </div>
      ) : (
        <div>
          <h2>Room ID: {roomId}</h2>
          <MainDiv messages={messages} />
          <ChatInput socket={socket} roomId={roomId} username={username} />
        </div>
      )}
    </div>
  );
}

export default App;
