import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import MainDiv from "./components/mainDiv";
import ChatInput from "./components/chatInput";

const socket = io("http://192.168.101.32:4000");

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [roomCreated, setRoomCreated] = useState(false);

  useEffect(() => {
    socket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    socket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("errorMessage", (error) => {
      alert(error);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("errorMessage");
    };
  }, []);

  const handleCreateRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter a username");
    }
  };

  const handleJoinRoom = () => {
    if (username.trim() && roomId.trim()) {
      socket.emit("joinRoom", { roomID: roomId, username });
    } else {
      alert("Please enter both username and room ID");
    }
  };

  return (
    <div className="container">
      {!roomCreated ? (
        <>
          <div className="header">Welcome to Chat App</div>
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Room ID</label>
            <input
              type="text"
              placeholder="Enter Room ID to join (optional)"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
          </div>
          <div className="buttons">
            <button onClick={handleCreateRoom}>Create Room</button>
            <button onClick={handleJoinRoom}>Join Room</button>
          </div>
        </>
      ) : (
        <div className="chat-room">
          <div className="header">Room: {roomId}</div>
          <div className="messages-container">
            <MainDiv messages={messages} />
          </div>
          <div className="chat-input">
            <ChatInput
              addMessage={(msg) =>
                socket.emit("sendMessage", { roomID: roomId, username, message: msg })
              }
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
