import { useState, useEffect } from "react";
import io from "socket.io-client";

import axios from 'axios';

function ChatInput() {
    const [message, setMessage] = useState('');
    const [messageList, setMessageList] = useState([]);
    const [selectedOption, setSelectedOption] = useState("");

    const [selectedOptionReceiver, setSelectedOptionReceiver] = useState("");

    const [socket, setSocket] = useState(null);

    useEffect(() => {
        // Connect to the socket server
        const newSocket = io("http://localhost:4000"); // Replace with your server URL
        setSocket(newSocket);

        // Listen for incoming messages
        newSocket.on('messageResponse', (newMessage) => {
            setMessageList((prev) => [...prev, newMessage]);
        });

        // Clean up socket connection when the component unmounts
        return () => {
            newSocket.disconnect();
        };
    }, []);
    const handleSelect = (event) => {
        setSelectedOption(event.target.value);
    };

    const handleSelectR = (event) => {
        setSelectedOption(event.target.value);
    };

    useEffect(()=>{
        const getMessage= async() => {
            const resp = await axios.get('http://localhost:4000/api')
            setMessageList(resp.data.messages)

        }
        getMessage()

    },[])

    console.log("message", messageList);


    const handleChange = (event) => {
        setMessage(event.target.value);
    };

    const handleSubmit = () => {

        if(selectedOption!==""){
            console.log('Message sent: ', message);
            setMessageList((prev) => ([...prev,{ message:message ,createdAt : new Date()}]))
            socket.emit(JSON.stringify({ message:message ,createdAt : new Date(),name :selectedOption}))
            setMessage('');
        }

    };
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSubmit();
        }
    }

    return (
        <>  
             <div>
            <label htmlFor="dropdown">Choose an option:</label>
            <select id="dropdown" value={selectedOption} onChange={handleSelect}>
                <option value="">--Select--</option>
                <option value="piyush">Piyush</option>
                <option value="ankita">ankita</option>
                <option value="hans">hans</option>
            </select>

            <p>You sender selected: {selectedOption}</p>
        </div>

        <div>
            <label htmlFor="dropdown">Choose an option:</label>
            <select id="dropdown" value={selectedOptionReceiver} onChange={handleSelect}>
                <option value="">--Select--</option>
                <option value="piyush">Piyush</option>
                <option value="ankita">ankita</option>
                <option value="hans">hans</option>
            </select>

            <p>You receiver  selected: {selectedOption}</p>
        </div>

            <div>

                {messageList.map(
                    (element, index) => {
                        return (<li key={index}>{element.message} </li>)
                    }
                )}
                <input id="ip"
                    type="text"
                    value={message}
                    onChange={handleChange}
                    onKeyDown={handleKeyDown}
                    placeholder="Type a message..."
                    className="chat-input"
                />
                <button id="sendBtn" onClick={handleSubmit}> <img id="senimg" src="src/images/buttonsend.png" alt="=>"></img> </button>
            </div>
        </>
    );
}

export default ChatInput;
