import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/mainDiv";
import "./index.css";

const socket = io("http://192.168.101.32:4000"); // Replace with your backend IP

function App() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState(""); // Define the message state here
  const [roomCreated, setRoomCreated] = useState(false);
  const [isJoiningRoom, setIsJoiningRoom] = useState(false);

  useEffect(() => {
    socket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    socket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setRoomCreated(true);
    });

    socket.on("errorMessage", (error) => {
      alert(error);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("errorMessage");
    };
  }, []);

  const handleCreateRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter a username");
    }
  };

  const handleJoinRoom = () => {
    if (username.trim() && roomId.trim()) {
      socket.emit("joinRoom", { roomID: roomId, username: username });
      setIsJoiningRoom(true);
    } else {
      alert("Please enter both a username and room ID");
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      socket.emit("sendMessage", { roomID: roomId, username: username, message: message });
      setMessage(""); // Clear message input after sending
    }
  };

  return (
    <div className="App">
      <h1 className="app-title">Chat Application</h1>

      {!roomCreated ? (
        <div className="room-selection">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input-field"
          />
          <input
            type="text"
            placeholder="Enter Room ID (optional)"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="input-field"
          />
          <div className="button-group">
            <button onClick={handleCreateRoom} className="action-btn">
              Create Room
            </button>
            <button onClick={handleJoinRoom} className="action-btn">
              Join Room
            </button>
          </div>
        </div>
      ) : (
        <div className="chat-room">
          <h2 className="room-id">Room ID: {roomId}</h2>
          <div className="messages-container">
            <MainDiv messages={messages} />
          </div>
          <ChatInput
            socket={socket}
            roomId={roomId}
            username={username}
            message={message}
            setMessage={setMessage}
            handleSendMessage={handleSendMessage}
          />
        </div>
      )}
    </div>
  );
}

export default App;
