import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import ChatInput from "./components/ChatInput";
import MainDiv from "./components/MainDiv";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [roomName, setRoomName] = useState("");
  const [roomId, setRoomId] = useState("");
  const [messages, setMessages] = useState([]);
  const [currentScreen, setCurrentScreen] = useState("welcome"); // "room" or "chat"
  const socketRef = useRef(null);



  useEffect(() => {
    socketRef.current = io("http://192.168.101.32:4000", {
      transports: ["websocket"],
    });

    const socket = socketRef.current;

    socket.on("connect", () => console.log("Connected to server"));
    socket.on("receiveMessage", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });
    socket.on("roomCreated", (data) => {
      setRoomId(data.roomID);
      setCurrentScreen("chat");
    });
    socket.on("roomJoined", (data) => {
      setRoomId(data.roomID);
      setCurrentScreen("chat");
    });

    return () => socket.disconnect();
  }, []);

  const handleCreateRoom = () => {
    if (username.trim() && roomName.trim()) {
      socketRef.current.emit("createRoom", { username, roomName });
    } else {
      alert("Please fill all fields.");
    }
  };

  const handleJoinRoom = () => {
    if (username.trim() && roomId.trim()) {
      socketRef.current.emit("joinRoom", { username, roomID: roomId });
    } else {
      alert("Please fill all fields.");
    }
  };

  const handleSendMessage = (message) => {
    if (message.trim()) {
      socketRef.current.emit("sendMessage", {
        roomID: roomId,
        username,
        message,
      });
    }
  };

  return (
    <div className="app">
      {currentScreen === "welcome" && (
        <div className="container">
          <h2>Welcome to Chat App</h2>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={() => setCurrentScreen("room")}>Submit</button>
        </div>
      )}
      {currentScreen === "room" && (
        <div className="container">
          <h2>Create or Join Room</h2>
          <input
            type="text"
            placeholder="Enter Room Name"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Room ID (for joining)"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <button onClick={handleCreateRoom}>Create Room</button>
          <button onClick={handleJoinRoom}>Join Room</button>
        </div>
      )}
      {currentScreen === "chat" && (
        <div className="container">
          <div className="chat-header">
            Room: {roomName} | ID: {roomId}
          </div>
          <div className="messages-container">
            <MainDiv messages={messages} />
          </div>
          <ChatInput sendMessage={handleSendMessage} />
        </div>
      )}
    </div>
  );
}

export default App;
