import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import ChatInput from "./ChatInput";
import MainDiv from "./MainDiv";

// Backend ka socket server
const socket = io("http://localhost:4000");

function App() {
  const [messages, setMessages] = useState([]);
  const [roomID, setRoomID] = useState("");
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  // Message receive karna
  socket.on("receiveMessage", (data) => {
    setMessages((prevMessages) => [...prevMessages, data]);
  });

  const handleCreateRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username, (data) => {
        setRoomID(data.roomID);
        navigate(`/chat/${data.roomID}`, { state: { roomID: data.roomID, username } });
      });
    } else {
      alert("Please enter a username");
    }
  };

  const handleJoinRoom = () => {
    if (username.trim()) {
      navigate(`/chat/${roomID}`, { state: { roomID, username } });
    } else {
      alert("Please enter a username");
    }
  };

  const handleSendMessage = (message) => {
    socket.emit("sendMessage", { roomID, username, message });
  };

  return (
    <div>
      {!roomID ? (
        <div>
          <h1>Join or Create a Room</h1>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleCreateRoom}>Create Room</button>
          <button onClick={handleJoinRoom}>Join Room</button>
        </div>
      ) : (
        <div>
          <h1>Room ID: {roomID}</h1>
          <MainDiv messages={messages} />
          <ChatInput addMessage={handleSendMessage} />
        </div>
      )}
    </div>
  );
}

export default App;
