import React, { useEffect, useState } from "react";
import ChatInput from "./ChatInput";
import { io } from "socket.io-client";

const socket = io("http://localhost:4000");

function MainDiv({ username }) {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        socket.on("messageResponse", (data) => {
            setMessages((prevMessages) => [...prevMessages, data]);
        });

        return () => socket.disconnect();
    }, []);

    const handleSendMessage = (message) => {
        socket.emit("message", JSON.stringify({ username, text: message }));
    };

    return (
        <div id="mDiv" style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
            <div id="div1" style={{ flex: 1, overflowY: "auto", padding: "10px", border: "1px solid black" }}>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.username}:</strong> {msg.text}
                    </div>
                ))}
            </div>
            <div id="div2" style={{ padding: "10px", border: "1px solid black" }}>
                <ChatInput onSend={handleSendMessage} />
            </div>
        </div>
    );
}

export default MainDiv;
